class ImplementationPlan:
    def __init__(self, client, objectives):
        self.client = client
        self.objectives = objectives
        self.status = "in_planning"

    def generate_proposal(self):
        if self.objectives:
            self.status = "proposal_ready"
            return "Personalized proposal generated!"
        else:
            return "Define objectives first."
