class PlanImplementacion:
    def __init__(self, cliente, objetivos):
        self.cliente = cliente
        self.objetivos = objetivos
        self.estado = "en_planificacion"

    def generar_propuesta(self):
        if self.objetivos:
            self.estado = "propuesta_lista"
            return "Propuesta personalizada generada!"
        else:
            return "Definir objetivos primero."
